import React,{useContext}  from "react";
import Anime from "./Anime";
import { Container, Heading } from "../styles/AnimeListStyles";
//  import {ThemeContext} from '../contexts/theme.context';



function AnimeTypeList({ animes, type}) {


  //  const {isDarkMode} = useContext(ThemeContext);
  console.log('AnimeTypeList Component Render');

  return (
    <Container tRow='20px auto'>
      <Heading isDark={false}>{type}</Heading>
      {animes.map(anime => (
        <Anime
          key={anime.id}
          anime={anime}
          darkTheme={false}
        />
      ))}
    </Container>
  );
}

export default  AnimeTypeList;
